    this.initAds();
    this.calcNextArr();
    if (kongregate!==undefined&&kid==0) {
        function onKongregateInPageLogin() {
            kid = kongregate.services.getUserId();
            if (kid!==0 && pfdata===undefined) {
                _this.relog();
            }
        }
        kongregate.services.addEventListener("login", onKongregateInPageLogin);
    }
    this.sync();

